static/styles.css.content:
:root {
      --bg-color: #f8f9fa;
      --text-color: #212529;
      --navbar-bg: #0d6efd;
      --badge-bg: #0d6efd;
      --card-bg: #fff;
      --accent: #0d6efd;
    }
    body.dark-mode {
      --bg-color: #01002c;
      --text-color: #f1f1f1;
      --navbar-bg: #1a1e2d;
      --badge-bg: #4dabf7;
      --card-bg: rgba(255 255 255 / 0.05);
      --accent: #4dabf7;
    }
    body {
      background-color: var(--bg-color);
      color: var(--text-color);
      font-family: 'Inter', sans-serif;
      margin: 0;
      min-height: 100vh;
    }
    nav.navbar {
      background-color: var(--navbar-bg);
      display: flex;
      align-items: center;
      padding: 0.5rem 1rem;
      position: sticky;
      top: 0;
      z-index: 1000;
    }
    .navbar-brand {
      color: #fff;
      font-weight: 600;
      font-size: 1.25rem;
      text-decoration: none;
    }
    .theme-toggle {
      margin-left: auto;
      background: none;
      border: none;
      font-size: 1.5rem;
      color: #fff;
      cursor: pointer;
    }
    .container {
      max-width: 1100px;
      margin: 2rem auto;
      padding: 1rem 2rem;
      background: var(--card-bg);
      border-radius: 16px;
      box-shadow: 0 12px 30px rgba(0,0,0,0.3);
      backdrop-filter: blur(18px);
      border: 1px solid rgba(255 255 255 / 0.08);
    }
    .video-wrapper {
      width: 80%;
      margin: 2rem auto;
    }
    .video-frame {
      width: 100%;
      height: 500px;
      border: none;
      border-radius: 16px;
      background: var(--card-bg);
      box-shadow: 0 8px 20px rgba(0,0,0,0.2);
    }
    .streams-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(340px, 1fr));
      gap: 2rem;
    }
    .stream-card {
      background: var(--card-bg);
      border-radius: 2px;
      box-shadow: 0 4px 15px rgba(13, 110, 253, 0.1);
      transition: transform 0.3s ease, box-shadow 0.3s ease;
      display: flex;
      flex-direction: column;
    }
    .stream-card:hover {
      transform: translateY(-6px);
      box-shadow: 0 6px 20px rgba(13, 110, 253, 0.2);
    }
    .stream-video {
      position: relative;
      padding-top: 56.25%;
      overflow: hidden;
      border-radius: 12px 12px 0 0;
    }
    .stream-video iframe {
      position: absolute;
      width: 100%;
      height: 100%;
      top: 0;
      left: 0;
      border: none;
    }
    .stream-content {
      padding: 1rem;
      flex-grow: 1;
    }
    .stream-title {
      font-size: 1.15rem;
      font-weight: 600;
      margin-bottom: 0.5rem;
    }
    .chat-frame {
      width: 100%;
      height: 500px;
      border: none;
      border-radius: 16px;
    }
    .local-chat {
      height: 200px;
      overflow-y: auto;
      padding: 1rem;
      background: var(--card-bg);
      border: 1px solid rgba(255,255,255,0.1);
      border-radius: 12px;
      font-size: 0.9rem;
    }
    .local-chat-form input {
      border-radius: 30px;
      background: transparent;
      color: var(--text-color);
      border: 1px solid rgba(255,255,255,0.1);
      padding-left: 1rem;
    }
    .local-chat-form input::placeholder {
      color: var(--text-color);
      opacity: 0.6;
    }
    .local-chat-form button {
      border-radius: 30px;
      background-color: var(--accent);
      color: #fff;
      padding: 0 1.25rem;
    }
    .btn-outline-primary {
      border-color: var(--accent);
      color: var(--accent);
    }
    .btn-outline-primary:hover {
      background-color: var(--accent);
      color: #fff;
    }
    @media (max-width: 768px) {
      .video-frame, .chat-frame {
        height: 300px;
      }
    }

